import{_ as e,c as t,o,e as i}from"./app.0e4eb262.js";const m='{"title":"Tooltip V2","description":"","frontmatter":{"title":"Tooltip V2","lang":"zh-CN"},"headers":[{"level":2,"title":"\u57FA\u7840\u7528\u6CD5","slug":"\u57FA\u7840\u7528\u6CD5"},{"level":2,"title":"\u53EF\u8BBF\u95EE\u6027\u53CB\u597D\u7684\u6587\u5B57\u63D0\u793A","slug":"\u53EF\u8BBF\u95EE\u6027\u53CB\u597D\u7684\u6587\u5B57\u63D0\u793A"},{"level":2,"title":"\u8F6C\u573A/\u52A8\u753B","slug":"\u8F6C\u573A-\u52A8\u753B"},{"level":3,"title":"\u5B8C\u5168\u8FC7\u6E21","slug":"\u5B8C\u5168\u8FC7\u6E21"},{"level":2,"title":"\u6E32\u67D3\u5230\u6839\u5143\u7D20","slug":"\u6E32\u67D3\u5230\u6839\u5143\u7D20"},{"level":2,"title":"\u5E26\u7BAD\u5934","slug":"\u5E26\u7BAD\u5934"}],"relativePath":"zh-CN/component/tooltip-v2.md"}',a={},n=i('<h1 id="tooltip-v2" tabindex="-1">Tooltip V2 <a class="header-anchor" href="#tooltip-v2" aria-hidden="true">#</a></h1><p>\u5BF9\u4E8E\u73B0\u6709\u7684 Tooltip \u6765\u8BF4\uFF0C\u5B83\u6709\u592A\u591A\u7684 API\uFF0C\u8FD9\u4E9B API \u4E0D\u662F\u975E\u5E38\u76F4\u89C2\u548C\u6613\u4E8E\u83B7\u53D6\u7684\u3002 \u6240\u4EE5\u6211\u4EEC\u521B\u5EFA\u4E86\u8FD9\u4E2A\u7B80\u5355\u5F97\u591A\u7684\u5DE5\u5177\u63D0\u793A\u3002\u5B83\u53EA\u505A\u4E86\u4E00\u4EF6\u7B80\u5355\u7684\u4E8B\u60C5 \u2014 \u663E\u793A\u63D0\u793A\u4FE1\u606F\u3002 \u867D\u7136\u4E24\u8005\u7ED3\u6784\u76F8\u4F3C\uFF0C\u4F46 API \u662F\u4E0D\u540C\u7684\u3002 \u5728\u8FD9\u4E2A\u7248\u672C\u4E2D\uFF0C\u6211\u4EEC\u5355\u72EC\u63D0\u4F9B\u4E86\u7EC4\u4EF6\u3002\u4F60\u53EF\u4EE5\u901A\u8FC7\u4F7F\u7528\u7EC4\u4EF6\u6765\u521B\u5EFA\u81EA\u5DF1\u7684\u5DE5\u5177\u63D0\u793A\u3002</p><h2 id="\u57FA\u7840\u7528\u6CD5" tabindex="-1">\u57FA\u7840\u7528\u6CD5 <a class="header-anchor" href="#\u57FA\u7840\u7528\u6CD5" aria-hidden="true">#</a></h2><p>\u5728\u56FE\u6807\u4E0A\u4EE5 <strong>Hover</strong> \u6216\u8005 <strong>tab</strong> \u65B9\u5F0F\u89E6\u53D1\u6587\u5B57\u63D0\u793A\u3002</p><h2 id="\u53EF\u8BBF\u95EE\u6027\u53CB\u597D\u7684\u6587\u5B57\u63D0\u793A" tabindex="-1">\u53EF\u8BBF\u95EE\u6027\u53CB\u597D\u7684\u6587\u5B57\u63D0\u793A <a class="header-anchor" href="#\u53EF\u8BBF\u95EE\u6027\u53CB\u597D\u7684\u6587\u5B57\u63D0\u793A" aria-hidden="true">#</a></h2><h2 id="\u8F6C\u573A-\u52A8\u753B" tabindex="-1">\u8F6C\u573A/\u52A8\u753B <a class="header-anchor" href="#\u8F6C\u573A-\u52A8\u753B" aria-hidden="true">#</a></h2><p>You may set transition/animation via <code>CSS</code> animation/transition or <a href="https://vuejs.org/guide/built-ins/transition.html#transition" target="_blank" rel="noopener noreferrer">Transition</a> component for your tooltip content when opening.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>By default, tooltip v2 ONLY allows a half-way transition/animation which only occurs when the tooltip is shown. This is because the implementation uses <code>v-if</code> to show/hide the tooltip. When closing, the content element is removed from DOM, so that the transition / animation will be interrupted.</p></div><h3 id="\u5B8C\u5168\u8FC7\u6E21" tabindex="-1">\u5B8C\u5168\u8FC7\u6E21 <a class="header-anchor" href="#\u5B8C\u5168\u8FC7\u6E21" aria-hidden="true">#</a></h3><p>Of course, you can have full transition for your tooltip content. But this requires using <a href="https://vuejs.org/guide/built-ins/transition.html#transition" target="_blank" rel="noopener noreferrer">Transition</a> component, checkout the demo below.</p><h2 id="\u6E32\u67D3\u5230\u6839\u5143\u7D20" tabindex="-1">\u6E32\u67D3\u5230\u6839\u5143\u7D20 <a class="header-anchor" href="#\u6E32\u67D3\u5230\u6839\u5143\u7D20" aria-hidden="true">#</a></h2><p>By default, unlike <a href="./tooltip.html">tooltip v1</a>, the tooltip will be rendered to the body element. <strong>tooltip v2</strong> will render to where the trigger/reference element is. But you can still render to to the root element by using <code>Teleport</code> component.</p><h2 id="\u5E26\u7BAD\u5934" tabindex="-1">\u5E26\u7BAD\u5934 <a class="header-anchor" href="#\u5E26\u7BAD\u5934" aria-hidden="true">#</a></h2>',13),r=[n];function l(s,h,d,c,p,u){return o(),t("div",null,r)}var _=e(a,[["render",l]]);export{m as __pageData,_ as default};
